    private void btCadastrarActionPerformed(java.awt.event.ActionEvent evt) {                                            
        Pessoa p = new Pessoa();
        
        p.setNome(txtNomeCliente.getText());
        p.setCpf(txtCpfCliente.getText());
        p.setTelefone(txtTelefoneCliente.getText());
        
        JOptionPane.showMessageDialog(rootPane, "O usu√°rio: " 
        + p.getNome() + " foi cadastrado com sucesso!");
        
        listaClientes.add(p);
        txtNomeCliente.setText("");
        txtCpfCliente.setText("");
        txtTelefoneCliente.setText("");
        
        p.registrarCliente();
        
    }                                           
      
                                         
