    private void btListarActionPerformed(java.awt.event.ActionEvent evt) {                                         
        for(Pessoa p : listaClientes) {
            JOptionPane.showMessageDialog(rootPane, "Telefone: " + p.getTelefone()
                    +"\nNome: " + p.getNome()
                    + "\nCpf: " + p.getCpf());
        }
    }                                        

